import React, { useState, useEffect } from "react";
import './App.css';

function App() {
const [inputarry , setInputarry] = useState([]);
const [inputdata, setInputdata]  = useState({

  name: "", 
  level: ""
});


  function changehandle(e){

    setInputdata({...inputdata, [e.target.name]: e.target.value})
  }

  let {name, rollNo} = inputdata; 
  function changehandle2(){
    setInputarry([...inputarry, {name , rollNo}])
    setInputdata({name:  "", level: ""})
  }

  return (
    <div className="App">
      <input type = "text" name = "name" value={inputdata.name} onChange = {changehandle} />
      <input type = "text" name = "level" value={inputdata.lavel} onChange = {changehandle} />

        <button onClick={changehandle}>Add</button><br></br>
        <button onClick={changehandle2}>Add</button><br></br>

        <table border={1} cellPadding={10}>
          <tr>
            <td>Name </td>
            <td>Level </td>
          </tr>
        </table>
    </div>
  );
}

export default App;
