import React, { Component } from 'react';
import './App.css';

class LogTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      logs: [],
      logText: '',
      level: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    const storedLogs = localStorage.getItem('logs');
    if (storedLogs) {
      this.setState({ logs: JSON.parse(storedLogs) });
    } else {
      localStorage.setItem('logs', JSON.stringify([]));
    }
  }

  handleChange(event) {
    this.setState({ logText: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const newLog = {
      text: this.state.logText,
      level: this.state.level,
      date: new Date().toLocaleString()
    };
    const updatedLogs = [...this.state.logs, newLog];
    localStorage.setItem('logs', JSON.stringify(updatedLogs));
    this.setState({ logs: updatedLogs, logText: '' , level: ''});
  }

  render() {
    return (
      <div className="log-table-container">
        <form onSubmit={this.handleSubmit}>
          <label>
            Add log:
            <input type="text" value={this.state.logText} onChange={this.handleChange} />
          </label>
          <button type="submit">Add</button>
        </form>
        <table className="log-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Level</th>
              <th>Log</th>
            </tr>
          </thead>
          <tbody>
            {this.state.logs.map((log, index) => (
              <tr key={index}>
                <td>{log.date}</td>
                <td>{log.text}</td>
                <td>{log.text}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default LogTable;
